#banner {
  position: relative;
  background-attachment: fixed;
  background-image: linear-gradient(rgba($color-black-primary, 0.7),
      rgba($color-black-primary, 0.7)),
    url(./../img/banner/banner1.jpg);
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center top;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  backface-visibility: hidden;
  -webkit-backface-visibility: hidden;

  .seperator-primary::after,
  .seperator-primary::before {
    background-color: $color-white-primary;
  }

  .banner-content {
    position: absolute;
    left: 50%;
    top: 55%;
    width: 60%;
    color: $color-white-primary;
    min-height: 40rem;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    @include transform-xy-minus50percent;

    &__slogan {
      font-size: 2.3rem;
    }

    &__heading {}

    &__sperator {
      margin: 0 !important;
    }

    &__para {
      margin-bottom: 2rem;

      p {
        font-size: 1.5rem;
        line-height: 2.5rem;
        width: 70%;
        margin: 0 auto;
        color: $color-white-secondary;
        text-align: center;
      }
    }

    &__btn {
      .banner-btn {
        box-shadow: none !important;
        width: 22rem;
        text-align: center;
      }

      .banner-btn--secondary {
        background: none;
        border: 1px solid $color-white-secondary;
        padding: 1.35rem 4.5rem;
        position: relative;

        &:hover {
          color: $color-primary;
        }

        &::before {
          background-color: $color-white-secondary;
          z-index: -1;
        }
      }
    }
  }


}

