      }
    }
  }

  // FOR ANIMATION ON-SCROLL
  .status-box-wrapper {
    @include AOS-fade-up-md;
    @include AOS-transition-transformCubicBezier-md;

    &.visible {
      @include AOS-fade-stop;
    }
  }
}

