#contact {
  margin-bottom: 4rem;
  background-color: $color-white-secondary;
  overflow: hidden;

  .heading-tertiary {
    color: $color-primary;
    margin-bottom: 1.7rem;
  }

  p {
    color: $color-secondary-light;
    margin-bottom: 3.5rem;
  }

  .contact--wrapper {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-flow: row wrap;

  }

  .contact-box {
    @include box-shadow--primary;
    padding: 5rem;
    background: $color-white-primary;
    min-height: 40rem;

    // on small screen device: screen width <= phone width
    @include respond-mediaQuery(phone) {
      width: 100%;
      margin: 0 0 1rem 0 !important;
      flex: none !important;
    }
  }

  .contact-box--left {
    flex: 1;
    margin-right: 2.5rem;
  }

  .contact-box--right {
    flex: 3;

    .contact-form {

      input,
      textarea {
        display: block;
        width: 100%;
        border: 0 0 0 1px solid;
        padding: 1rem 0;
        margin-bottom: 2rem;
        border: none;
        border-bottom: 1px solid $color-secondary-light;
        outline: none;
        font-size: inherit;
        font-family: inherit;
        color: $color-secondary-light;
        @include transition-delay-sm;

        &:focus {
          border-bottom: 1px solid $color-primary;
        }

        &::placeholder {
          color: $color-secondary-light;
        }

        &:-ms-input-placeholder {
          /* Internet Explorer 10-11 */
          color: $color-secondary-light;
        }

        &::-ms-input-placeholder {
          /* Microsoft Edge */
          color: $color-secondary-light;
        }
      }

      textarea {
        min-height: 9rem;
      }
    }

    .contact__btn {
      background: $color-secondary;
      z-index: 1;
    }
  }

  // FOR ANIMATION ON-SCROLL
  .contact--wrapper {
    .contact-box {
      @include AOS-transition-transformCubicBezier-md;
    }

    .contact-box:nth-child(odd) {
      @include AOS-fade-left-md;
    }

    .contact-box:nth-child(even) {
      @include AOS-fade-right-md;
    }

    &.visible .contact-box {
      @include AOS-fade-stop;

    }
  }
}
